PlayerAgent = {}function PlayerAgent:create(scene)        local playerLayer = PlayerLayer:create()    playerLayer:setPosition(0, 0)    scene:addChild(playerLayer, GameScene_PlayerLayer_ID, GameScene_PlayerLayer_ID)        local playerBulletLayer = PlayerBulletLayer:create()    playerBulletLayer:setPosition(0, 0)    scene:addChild(playerBulletLayer, GameScene_PlayerBulletLayer_ID, GameScene_PlayerBulletLayer_ID)    endfunction PlayerAgent:getPlayer()    return PlayerLayer:getPlayer()endfunction PlayerAgent:shot()    local player = PlayerLayer:getPlayer()    if player then        local x, y = player:getPosition()        PlayerBulletLayer:addNewBullet(x, y)    endendfunction PlayerAgent:moveBullets()    PlayerBulletLayer:moveBullets()endfunction PlayerAgent:playerHitten()    PlayerLayer:playerHitten()end